var egret;
(function (egret) {
    var web;
    (function (web) {
        /**
         * @private
         * ImageLoader 类可用于加载图像（JPG、PNG 或 GIF）文件。使用 load() 方法来启动加载。被加载的图像对象数据将存储在 ImageLoader.data 属性上 。
         */
        var WebImageLoader = (function (_super) {
            __extends(WebImageLoader, _super);
            function WebImageLoader() {
                _super.apply(this, arguments);
            }
            var __egretProto__ = WebImageLoader.prototype;
            /**
             * @private
             *
             * @param url
             * @param callback
             */
            __egretProto__.load = function (url, callback) {
                var self = this;
                var bitmapData = egret.BaseImageLoader._bitmapDataFactory[url];
                if (!bitmapData) {
                    bitmapData = document.createElement("img");
                    bitmapData.setAttribute("bitmapSrc", url);
                    egret.BaseImageLoader._bitmapDataFactory[url] = bitmapData;
                }
                if (bitmapData["avaliable"]) {
                    callback(0, bitmapData);
                    return;
                }
                if (!egret.BaseImageLoader.crossOrigin) {
                    if (bitmapData.hasAttribute("crossOrigin")) {
                        bitmapData.removeAttribute("crossOrigin");
                    }
                }
                else {
                    bitmapData.setAttribute("crossOrigin", egret.BaseImageLoader.crossOrigin);
                }
                var winURL = window["URL"] || window["webkitURL"];
                if (egret.BaseImageLoader._bitmapCallbackMap[url] == null) {
                    this._addToCallbackList(url, callback);
                    if (url.indexOf("data:") != 0 && url.indexOf("http:") != 0 && url.indexOf("https:") != 0 && web.Html5Capatibility._canUseBlob) {
                        var xhr = new XMLHttpRequest();
                        xhr.open("get", url, true);
                        xhr.responseType = "blob";
                        xhr.onerror = function () {
                            self._onError(url, bitmapData);
                        };
                        xhr.onload = function () {
                            if (this.status == 200) {
                                var blob = this.response;
                                bitmapData.onload = function () {
                                    winURL.revokeObjectURL(bitmapData.src); // 清除释放
                                    self._onLoad(url, bitmapData);
                                };
                                bitmapData.onerror = function () {
                                    self._onError(url, bitmapData);
                                };
                                bitmapData.src = winURL.createObjectURL(blob);
                            }
                            else {
                                self._onError(url, bitmapData);
                            }
                        };
                        xhr.send();
                    }
                    else {
                        bitmapData.onload = function () {
                            self._onLoad(url, bitmapData);
                        };
                        bitmapData.onerror = function () {
                            self._onError(url, bitmapData);
                        };
                        bitmapData.src = url;
                    }
                }
                else {
                    this._addToCallbackList(url, callback);
                }
            };
            /**
             * @private
             *
             * @param bitmapData
             */
            WebImageLoader.disposeBitmapData = function (bitmapData) {
                WebImageLoader.deleteWebGLTexture(bitmapData);
                bitmapData.onload = null;
                bitmapData.onerror = null;
                //替换为1x1透明图片
                bitmapData.src = "data:image/png;base64,";
                bitmapData["avaliable"] = false;
                console.log("_disposeForCanvas");
            };
            /**
             * @private
             *
             * @param bitmapData
             */
            WebImageLoader.deleteWebGLTexture = function (bitmapData) {
                if (bitmapData) {
                    var webGLTexture = bitmapData.webGLTexture;
                    if (webGLTexture) {
                        for (var key in webGLTexture) {
                            var glTexture = webGLTexture[key];
                            var gl = glTexture.glContext;
                            gl.deleteTexture(glTexture);
                        }
                    }
                    bitmapData.webGLTexture = null;
                }
            };
            return WebImageLoader;
        })(egret.BaseImageLoader);
        web.WebImageLoader = WebImageLoader;
        WebImageLoader.prototype.__class__ = "egret.web.WebImageLoader";
        egret.registerClass(WebImageLoader,"egret.web.WebImageLoader");
        egret.ImageLoader = WebImageLoader;
        egret.ImageLoader.disposeBitmapData = WebImageLoader.disposeBitmapData;
    })(web = egret.web || (egret.web = {}));
})(egret || (egret = {}));
